
"use strict"




const fs        = require('fs')
const commons   = require('cloud-init-compile/commons/commons')
const constants = require('cloud-init-compile/commons/constants')





const foofoofoofoo = contents => {

	const scripts = [
		constants.shebangs.bash
	].concat(
		contents.map(content => {

			return content

		})
	)

	console.log( scripts )

}





const bundleContent = (fpaths, opts, callback) => {

	commons.async.map(fpaths, fs.readFile, results => {

		results.forEach(result => {

			if (result.err) {
				return console.error('TODO')
			}

		})

		foofoofoofoo( results.map(result => {
			return result.result.toString( )
		}) )

	})

}





module.exports = bundleContent
