#!/usr/bin/env node

"use strict"





const doc = `
Name:
	cloud-init-compile â€” compile multiple scripts into a single script.

Usage:
	cloud-init-compile (-r FPATH | --run FPATH) [--gzip] [--] <fpath>...
	cloud-init-compile (-h | --help | --version)

Options:
	-r FPATH, --run FPATH    The path or basename of the script to execute.
	-h, --help               Display this documentation.

Arguments:
	<fpath>...      The files to load content from.
	<runpath>...    The files to execute.
`





const docopt  = require('docopt').docopt
const compile = require('cloud-init-compile/app/compile')
const args    = docopt(doc)





compile(args)
