#!/usr/bin/env node

"use strict"





const doc = `
Name:
	clic (cloud-init-compile) â€” compile multiple scripts into a single script.

Usage:
	clic (-r FPATH | --run FPATH) [-z|--gzip] [--] <fpath>...
	clic (-h | --help | --version)

Description:

	* All files are assumed to be UTF-8 encoded.
	* The highest level of compression is used when gzip is enabled.

Options:
	-z, --gzip               Gzip the output.
	-r FPATH, --run FPATH    The path or basename of the script to execute.
	-h, --help               Display this documentation.

Arguments:
	<fpath>...      The files to load content from.
	<runpath>...    The files to execute.
`





const docopt  = require('docopt').docopt
const compile = require('cloud-init-compile/app/compile')
const args    = docopt(doc)





compile(args, (err, content) => {
	console.log(content)
})
