
"use strict"




const commons = {
	async: { }
}



commons.async.map = (data, fn, callback, acc) => {

	if (data.length === 0) {
		return callback(acc)
	} else {

		fn(data[0], (err, result) => {
			commons.async.map( data.slice(1), fn, callback, (acc || [ ]).concat({err,result}) )
		})

	}

}




module.exports = commons
